// src/pages/SmartCenter/WeatherAlert.jsx
import React, { useEffect, useState } from 'react';
import { 
  Card, 
  Typography, 
  Alert, 
  Space, 
  Spin,
  message
} from 'antd';
import {
  ThunderboltOutlined,
  CloudOutlined,
  WarningOutlined,
  ExperimentOutlined
} from '@ant-design/icons';
import './WeatherAlert.css';

const { Title, Text } = Typography;

const WeatherAlert = ({ selectedPoint }) => {
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (selectedPoint) {
      getWeatherByLocation([selectedPoint.lng, selectedPoint.lat]);
    } else {
      // ÈªòËÆ§ÊòæÁ§∫ËàüÂ±±Ê∏îÂú∫Â§©Ê∞î
      getWeatherByLocation([122.2, 29.98]);
    }
  }, [selectedPoint]);

  const FISHING_AREAS = {
    'Ê∏§Êµ∑Ê∏îÂú∫': { lng: 120.730618, lat: 40.152939 }, // Â∞èÈæôÂ±±Â≤õÁ≤æÁ°ÆÂùêÊ†á
    'ÂçóÊµ∑Ê∏îÂú∫': { lng: 112.887598, lat: 20.613384 }, // Ê≠£ÂíÄÂ±±Á≤æÁ°ÆÂùêÊ†á
    'ÂåóÈÉ®ÊπæÊ∏îÂú∫': { lng: 109.47646, lat: 21.48482 }   // ÂπøÊóÖÁôΩÊ≤ôÊπæÂ±ïÁ§∫‰∏≠ÂøÉ
  };

  const getWeatherByLocation = (lnglat) => {
    setLoading(true);
    const [lng, lat] = lnglat;

    fetch(`https://restapi.amap.com/v3/weather/weatherInfo?key=9238743e5d83039e6f3486a437f5eb0d&location=${lng},${lat}&extensions=base`)
      .then(res => res.json())
      .then(data => {
        if (data.status === '1' && data.lives?.length > 0) {
          const weather = data.lives[0];
          const specialRecommendation = generateSpecialRecommendation(weather);
          setWeatherData({
            ...weather,
            recommendation: {
              ...generateRecommendations(weather),
              ...specialRecommendation
            }
          });
        } else {
          throw new Error('Â§©Ê∞îÊï∞ÊçÆ‰∏çÂèØÁî®');
        }
      })
      .catch(() => {
        const backupLnglat = [122.2, 29.98]; // ËàüÂ±±Â§áÁî®ÂùêÊ†á
        fetch(`https://restapi.amap.com/v3/weather/weatherInfo?key=9238743e5d83039e6f3486a437f5eb0d&location=${backupLnglat[0]},${backupLnglat[1]}`)
          .then(res => res.json())
          .then(backupData => {
            if (backupData.status === '1') {
              setWeatherData({
                ...backupData.lives[0],
                isBackup: true
              });
              message.warning('ÂΩìÂâçÊ∏îÂú∫Êï∞ÊçÆÂºÇÂ∏∏ÔºåÂ∑≤ÊòæÁ§∫ËàüÂ±±ÂèÇËÄÉÂ§©Ê∞î');
            }
          });
      })
      .finally(() => setLoading(false));
  };

  const generateSpecialRecommendation = (weather) => {
    const area = Object.keys(FISHING_AREAS).find(key => 
      Math.abs(FISHING_AREAS[key].lng - weather.longitude) < 0.1 &&
      Math.abs(FISHING_AREAS[key].lat - weather.latitude) < 0.1
