// src/pages/SmartCenter/WeatherAlert.jsx
import React, { useEffect, useState } from 'react';
import { Card, Typography, Alert, Space, Spin } from 'antd';
import {
  ThunderboltOutlined,
  CloudOutlined,
  WarningOutlined,
  ExperimentOutlined
} from '@ant-design/icons';
import './WeatherAlert.css';

const { Title, Text } = Typography;

// ÂõõÂ§ßÊ∏îÂú∫ÈªòËÆ§ÂùêÊ†á
const DEFAULT_FISHING_AREAS = [
  {name: "ËàüÂ±±Ê∏îÂú∫", position: [122.2, 29.98]},
  {name: "Ê∏§Êµ∑Ê∏îÂú∫", position: [120.5, 38.9]},
  {name: "ÂçóÊµ∑Ê∏îÂú∫", position: [113.5, 21.5]},
  {name: "ÂåóÈÉ®ÊπæÊ∏îÂú∫", position: [108.3, 21.5]}
];

const WeatherAlert = ({ selectedPoint }) => {
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(false);
  const [currentLocation, setCurrentLocation] = useState(DEFAULT_FISHING_AREAS[0]);

  const getWeatherByLocation = async (lnglat) => {
    try {
      setLoading(true);

      // ÂÖàÈÄöËøáÈÄÜÂú∞ÁêÜÁºñÁ†ÅËé∑ÂèñÂüéÂ∏Çadcode
      const geoRes = await fetch(`https://restapi.amap.com/v3/geocode/regeo?key=9238743e5d83039e6f3486a437f5eb0d&location=${lnglat[0]},${lnglat[1]}`);
      const geoData = await geoRes.json();

      if (geoData.status !== '1') {
        throw new Error(geoData.info || 'Ëé∑ÂèñÂú∞ÁêÜ‰ΩçÁΩÆÂ§±Ë¥•');
      }

      const adcode = geoData.regeocode.addressComponent.adcode;

      // ‰ΩøÁî®adcodeÊü•ËØ¢Â§©Ê∞î
      const weatherRes = await fetch(`https://restapi.amap.com/v3/weather/weatherInfo?key=9238743e5d83039e6f3486a437f5eb0d&city=${adcode}`);
      const weatherData = await weatherRes.json();

      if (weatherData.status !== '1' || !weatherData.lives || weatherData.lives.length === 0) {
        throw new Error(weatherData.info || 'Â§©Ê∞îÊï∞ÊçÆËé∑ÂèñÂ§±Ë¥•');
      }

      setWeatherData(weatherData.lives[0]);
      setCurrentLocation({
        name: geoData.regeocode.formatted_address || 'ÂΩìÂâç‰ΩçÁΩÆ',
        position: lnglat
      });
    } catch (error) {
      console.error('Â§©Ê∞îÊï∞ÊçÆÂä†ËΩΩÂ§±Ë¥•:', error);
      // Â¶ÇÊûúÂá∫ÈîôÔºå‰ΩøÁî®ÈªòËÆ§Êï∞ÊçÆ
      setWeatherData({
        temperature: '--',
        windSpeed: '--',
        humidity: '--'
      });
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    if (selectedPoint) {
      getWeatherByLocation([selectedPoint.lng, selectedPoint.lat]);
    } else {
      // ÈªòËÆ§ÊòæÁ§∫Á¨¨‰∏Ä‰∏™Ê∏îÂú∫ÁöÑÂ§©Ê∞î
      getWeatherByLocation(DEFAULT_FISHING_AREAS[0].position);
    }
  }, [selectedPoint]);

  return (
    <Card className="weather-alert-card">
      <Title level={4} className="weather-alert-title">Â§©Ê∞îÈ¢ÑË≠¶</Title>
      <Text>ÂΩìÂâçÂå∫Âüü: {currentLocation.name} (ÁªèÂ∫¶: {currentLocation.position[0]}, Á∫¨Â∫¶: {currentLocation.position[1]})</Text>
      {selectedPoint && (
        <Text>ÂΩìÂâçÈÄâÊã©ÁÇπ: ÁªèÂ∫¶ {selectedPoint.lng}, Á∫¨Â∫¶ {selectedPoint.lat}</Text>
      )}
      {loading ? (
        <Spin tip="Âä†ËΩΩÂ§©Ê∞î‰ø°ÊÅØ‰∏≠..." />
      ) : (
        <>
          <div className="weather-status">
            <div className="weather-icon-wrapper">
              <CloudOutlined className="weather-icon" />
            </div>
            <div className="weather-details">
              <Text className="weather-info" style={{ color: '#fff' }}>üå° ÂΩìÂâçÊ∏©Â∫¶Ôºö{weatherData?.temperature ?? '--'}¬∞C</Text>
              <Text className="weather-info" style={{ color: '#fff' }}>üå¨ È£éÈÄüÔºö{weatherData?.windSpeed ?? '--'} m/s</Text>
              <Text className="weather-info" style={{ color: '#fff' }}>üíß ÊπøÂ∫¶Ôºö{weatherData?.humidity ?? '--'}%</Text>
            </div>
          </div>

          <div className="ai-advice">
            <div className="ai-advice-header">
              <ExperimentOutlined className="ai-icon" />
              <Text className="ai-title">AI Êô∫ËÉΩÂª∫ËÆÆ</Text>
            </div>
            <div className="ai-text">
              <Text className="weather-info" style={{ color: '#fff' }}>üå° Ê∏©Â∫¶Âª∫ËÆÆÔºö{weatherData?.recommendation?.temperature ?? '--'}</Text>
              <Text className="weather-info" style={{ color: '#fff' }}>üß™ pH Âª∫ËÆÆÔºö{weatherData?.recommendation?.ph ?? '--'}</Text>
              <Text className="weather-info" style={{ color: '#fff' }}>üßÇ ÁõêÂ∫¶Âª∫ËÆÆÔºö{weatherData?.recommendation?.salinity ?? '--'}</Text>
              <Text className="weather-info" style={{ color: '#fff' }}>‚òÄÔ∏è ÂÖâÁÖßÂª∫ËÆÆÔºö{weatherData?.recommendation?.light ?? '--'}</Text>
              <Text className="weather-info" style={{ color: '#fff' }}>üíß Ê∫∂Ê∞ßÂª∫ËÆÆÔºö{weatherData?.recommendation?.oxygen ?? '--'}</Text>
            </div>
          </div>

          <Space direction="vertical" className="weather-warning-list">
            {weatherData?.alerts?.length > 0 && weatherData.alerts.map((alert, index) => (
              <Alert
                key={index}
                message={<span style={{ fontSize: '16px', color: '#fff' }}>{alert.message}</span>}
                type={alert.type} // "warning" | "error" | "info"
                showIcon
                icon={alert.type === 'error' ? <ThunderboltOutlined /> : <WarningOutlined />}
              />
            ))}
          </Space>
        </>
      )}
    </Card>
  );
};

export default WeatherAlert;
