// src/pages/SmartCenter/WeatherAlert.jsx
import React, { useEffect, useState } from 'react';
import { 
  Card, 
  Typography, 
  Alert, 
  Space, 
  Spin,
  message
} from 'antd';
import {
  ThunderboltOutlined,
  CloudOutlined,
  WarningOutlined,
  ExperimentOutlined
} from '@ant-design/icons';
import './WeatherAlert.css';

const { Title, Text } = Typography;

const WeatherAlert = ({ selectedPoint }) => {
  // Ê∏îÂú∫Êï∞ÊçÆÊò†Â∞Ñ
  const FISHING_AREA_DATA = {
    'Ê∏§Êµ∑Ê∏îÂú∫': {
      areaSize: '8000‰∫©',
      waterType: 'Êµ∑Ê∞¥',
      fishTypes: ['Â∞èÈªÑÈ±º', 'Â∏¶È±º', 'ÂØπËôæ']
    },
    'ËàüÂ±±Ê∏îÂú∫': {
      areaSize: '12000‰∫©', 
      waterType: 'Êµ∑Ê∞¥',
      fishTypes: ['Â§ßÈªÑÈ±º', 'Â∞èÈªÑÈ±º', 'Â∏¶È±º', 'Â¢®È±º']
    },
    'ÂçóÊµ∑Ê∏îÂú∫': {
      areaSize: '10000‰∫©',
      waterType: 'Êµ∑Ê∞¥',
      fishTypes: ['ÈáëÊû™È±º', 'È©¨È≤õÈ±º', 'Áü≥ÊñëÈ±º']
    },
    'ÂåóÈÉ®ÊπæÊ∏îÂú∫': {
      areaSize: '9000‰∫©',
      waterType: 'Êµ∑Ê∞¥',
      fishTypes: ['ÁªØÈ≤§', 'Á∫¢Á¨õÈ≤∑', 'ÈáëÁ∫øÈ±º']
    }
  };

  // ÁîüÊàêAIÂª∫ËÆÆ
  const generateRecommendations = (weather) => {
    const temp = parseFloat(weather.temperature);
    const humidity = parseFloat(weather.humidity);
    const windPower = parseInt(weather.windpower) || 0;

    // Âü∫Á°ÄÂª∫ËÆÆ
    const baseRecommendations = {
      temperature: temp > 28 ? 'È´òÊ∏©È¢ÑË≠¶ÔºöÂª∫ËÆÆÂ¢ûÂä†Ê∞¥‰ΩìÂæ™ÁéØÂíåÈÅÆÈò≥ËÆæÊñΩ' : 
                  temp < 10 ? '‰ΩéÊ∏©È¢ÑË≠¶ÔºöÊ≥®ÊÑè‰øùÊ∏©Êé™ÊñΩÔºåÂáèÂ∞ëÊäïÂñÇÈáè' : 'Ê∏©Â∫¶ÈÄÇÂÆúÂÖªÊÆñ',
      ph: 'Âª∫ËÆÆ‰øùÊåÅpHÂÄºÂú®7.5-8.5‰πãÈó¥ÔºåÊØèÊó•ÁõëÊµã',
      salinity: humidity > 80 ? 'È´òÊπøÂ∫¶ÂèØËÉΩÁ®ÄÈáäÁõêÂ∫¶ÔºåÂª∫ËÆÆÂ¢ûÂä†ÁõêÂ∫¶ÁõëÊµãÈ¢ëÁéá' : 'ÁõêÂ∫¶Ê≠£Â∏∏',
      light: 'Âª∫ËÆÆÊØèÊó•ÂÖâÁÖß8-10Â∞èÊó∂ÔºåÈÅøÂÖçÂº∫ÂÖâÁõ¥Â∞Ñ',
      oxygen: '‰øùÊåÅÊ∫∂Ê∞ßÈáèÂú®5mg/L‰ª•‰∏äÔºåÂøÖË¶ÅÊó∂‰ΩøÁî®Â¢ûÊ∞ßËÆæÂ§á',
      wind: windPower > 6 ? 'Â§ßÈ£éÈ¢ÑË≠¶ÔºöÊ£ÄÊü•ÁΩëÁÆ±Âõ∫ÂÆöÔºåÂáèÂ∞ëÊäïÂñÇ' : 'È£éÂäõÊù°‰ª∂Ê≠£Â∏∏'
    };

    // Ê∏îÂú∫ÁâπËâ≤Âª∫ËÆÆ
    const areaRecommendations = selectedPoint && FISHING_AREA_DATA[selectedPoint.name] ? {
      fishTips: `ÂΩìÂâçÈÄÇÂêàÂÖªÊÆñÔºö${FISHING_AREA_DATA[selectedPoint.name].fishTypes.join('„ÄÅ')}`,
      feeding: temp > 25 ? 'Âª∫ËÆÆÂ¢ûÂä†ÊäïÂñÇÈ¢ëÁéá' : 'ÊåâÂ∏∏ËßÑÊäïÂñÇÂç≥ÂèØ',
      management: windPower > 5 ? 'Ê≥®ÊÑèÊ£ÄÊü•ÁΩëÁÆ±Âõ∫ÂÆöÊÉÖÂÜµ' : 'ÂèØËøõË°åÂ∏∏ËßÑÁÆ°ÁêÜÊìç‰Ωú'
    } : {};

    return {
      ...baseRecommendations,
      ...areaRecommendations
    };
  };
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (selectedPoint) {
      getWeatherByLocation([selectedPoint.lng, selectedPoint.lat]);
    } else {
      // ÈªòËÆ§ÊòæÁ§∫ËàüÂ±±Ê∏îÂú∫Â§©Ê∞î
      getWeatherByLocation([122.2, 29.98]);
    }
  }, [selectedPoint]);

  const getWeatherByLocation = (lnglat) => {
    setLoading(true);
    console.log('ÂºÄÂßãËé∑ÂèñÂ§©Ê∞îÊï∞ÊçÆÔºå‰ΩçÁΩÆ:', lnglat);

    // È¶ñÂÖàÂ∞ùËØïËé∑ÂèñÁ≤æÁ°Æ‰ΩçÁΩÆÁöÑÂ§©Ê∞î
    fetch(`https://restapi.amap.com/v3/geocode/regeo?key=9238743e5d83039e6f3486a437f5eb0d&location=${lnglat[0]},${lnglat[1]}`)
      .then(res => {
        if (!res.ok) throw new Error('ÁΩëÁªúËØ∑Ê±ÇÂ§±Ë¥•');
        return res.json();
      })
      .then(geoData => {
        console.log('Âú∞ÁêÜ‰ΩçÁΩÆAPIËøîÂõû:', geoData);
        if (geoData.status !== '1') {
          throw new Error('Ëé∑ÂèñÂú∞ÁêÜ‰ΩçÁΩÆÂ§±Ë¥•');
        }
        const adcode = geoData.regeocode.addressComponent.adcode;
        return fetch(`https://restapi.amap.com/v3/weather/weatherInfo?key=9238743e5d83039e6f3486a437f5eb0d&city=${adcode}`);
      })
      .then(res => {
        if (!res.ok) throw new Error('Â§©Ê∞îËØ∑Ê±ÇÂ§±Ë¥•');
        return res.json();
      })
      .then(data => {
        console.log('Â§©Ê∞îAPIËøîÂõû:', data);
        if (data.status !== '1' || !data.lives || data.lives.length === 0) {
          // Â¶ÇÊûúÁ≤æÁ°Æ‰ΩçÁΩÆËé∑ÂèñÂ§±Ë¥•ÔºåÂ∞ùËØïËé∑ÂèñÈôÑËøëÂüéÂ∏ÇÁöÑÂ§©Ê∞î
          return fetch(`https://restapi.amap.com/v3/weather/weatherInfo?key=9238743e5d83039e6f3486a437f5eb0d&extensions=base&location=${lnglat[0]},${lnglat[1]}`);
        }
        setWeatherData(data.lives[0]);
        return null;
      })
      .then(nearbyData => {
        if (nearbyData) {
          return nearbyData.json().then(data => {
            if (data.status === '1' && data.lives && data.lives.length > 0) {
              setWeatherData(data.lives[0]);
            } else {
              throw new Error('Êó†Ê≥ïËé∑ÂèñÈôÑËøëÂüéÂ∏ÇÂ§©Ê∞îÊï∞ÊçÆ');
            }
          });
        }
      })
      .catch(error => {
        console.error('ËØ∑Ê±ÇÂ§±Ë¥•:', error);
        message.error('Ëé∑ÂèñÂ§©Ê∞îÊï∞ÊçÆÂ§±Ë¥•: ' + error.message);
      })
      .finally(() => setLoading(false));
  };

  return (
    <Card className="weather-alert-card">
      <Title level={4} className="weather-alert-title" style={{ color: '#fff' }}>Â§©Ê∞îÈ¢ÑË≠¶</Title>
      {selectedPoint && (
        <div style={{ marginBottom: 16 }}>
          <Text strong style={{ color: '#00eaff' }}>ÂΩìÂâçÊ∏îÂú∫: {selectedPoint.name}</Text>
          <div style={{ marginTop: 8 }}>
            <Text style={{ color: '#fff' }}>Èù¢ÁßØ: {FISHING_AREA_DATA[selectedPoint.name]?.areaSize || '--'}</Text>
            <Text style={{ color: '#fff', marginLeft: 16 }}>ÂÖªÊÆñÁ±ªÂûã: {FISHING_AREA_DATA[selectedPoint.name]?.waterType || '--'}</Text>
            <Text style={{ color: '#fff', display: 'block', marginTop: 4 }}>
              ‰∏ªË¶ÅÈ±ºÁßç: {FISHING_AREA_DATA[selectedPoint.name]?.fishTypes?.join('„ÄÅ') || '--'}
            </Text>
          </div>
        </div>
      )}
      {loading ? (
        <Spin tip="Âä†ËΩΩÂ§©Ê∞î‰ø°ÊÅØ‰∏≠..." />
      ) : (
        <>
          <div className="weather-status">
            <div className="weather-icon-wrapper">
              <CloudOutlined className="weather-icon" />
            </div>
            <div className="weather-details">
              <Text className="weather-info" style={{ color: '#fff' }}>üå° ÂΩìÂâçÊ∏©Â∫¶Ôºö{weatherData?.temperature ?? '--'}¬∞C</Text>
              <Text className="weather-info" style={{ color: '#fff' }}>üå¨ È£éÈÄüÔºö{weatherData?.windpower ?? weatherData?.windSpeed ?? '--'} Á∫ß</Text>
              <Text className="weather-info" style={{ color: '#fff' }}>üß≠ È£éÂêëÔºö{weatherData?.winddirection ?? '--'}</Text>
              <Text className="weather-info" style={{ color: '#fff' }}>üíß ÊπøÂ∫¶Ôºö{weatherData?.humidity ?? '--'}%</Text>
            </div>
          </div>

          <div className="ai-advice">
            <div className="ai-advice-header">
              <ExperimentOutlined className="ai-icon" />
              <Text className="ai-title">AI Êô∫ËÉΩÂª∫ËÆÆ</Text>
            </div>
            // Âú®AIÂª∫ËÆÆÈÉ®ÂàÜÊ∑ªÂä†Êñ∞Âª∫ËÆÆÈ°π
            <div className="ai-text">
              <Text className="weather-info" style={{ color: '#fff' }}>üå° Ê∏©Â∫¶Âª∫ËÆÆÔºö{weatherData?.recommendation?.temperature ?? '--'}</Text>
              <Text className="weather-info" style={{ color: '#fff' }}>üß™ pH Âª∫ËÆÆÔºö{weatherData?.recommendation?.ph ?? '--'}</Text>
              <Text className="weather-info" style={{ color: '#fff' }}>üßÇ ÁõêÂ∫¶Âª∫ËÆÆÔºö{weatherData?.recommendation?.salinity ?? '--'}</Text>
              <Text className="weather-info" style={{ color: '#fff' }}>‚òÄÔ∏è ÂÖâÁÖßÂª∫ËÆÆÔºö{weatherData?.recommendation?.light ?? '--'}</Text>
              <Text className="weather-info" style={{ color: '#fff' }}>üíß Ê∫∂Ê∞ßÂª∫ËÆÆÔºö{weatherData?.recommendation?.oxygen ?? '--'}</Text>
              <Text className="weather-info" style={{ color: '#fff' }}>üå¨ È£éÂäõÂª∫ËÆÆÔºö{weatherData?.recommendation?.wind ?? '--'}</Text>
              <Text className="weather-info" style={{ color: '#fff' }}>üçΩ ÊäïÂñÇÂª∫ËÆÆÔºö{weatherData?.recommendation?.feeding ?? '--'}</Text>
              <Text className="weather-info" style={{ color: '#fff' }}>üõ† ÁÆ°ÁêÜÂª∫ËÆÆÔºö{weatherData?.recommendation?.management ?? '--'}</Text>
            </div>
          </div>

          <Space direction="vertical" className="weather-warning-list">
            {weatherData?.alerts?.length > 0 && weatherData.alerts.map((alert, index) => (
              <Alert
                key={index}
                message={<span style={{ fontSize: '16px', color: '#fff' }}>{alert.message}</span>}
                type={alert.type} // "warning" | "error" | "info"
                showIcon
                icon={alert.type === 'error' ? <ThunderboltOutlined /> : <WarningOutlined />}
              />
            ))}
          </Space>
        </>
      )}
    </Card>
  );
};

export default WeatherAlert;
