// src/pages/MainInfo/MapSection.jsx
import React, { useEffect } from 'react';
import { Card } from 'antd';
import './MapSection.css';

const MapSection = () => {
  useEffect(() => {
    // 使用window.AMap代替直接使用AMap
    const map = new window.AMap.Map('map-container', {
      zoom: 5,
      center: [117.361, 35.894],
    });

    // 四大渔场坐标
    const fishingAreas = [
      {name: "舟山渔场", position: [122.2, 29.98]},
      {name: "渤海渔场", position: [120.5, 38.9]},
      {name: "南海渔场", position: [113.5, 21.5]},
      {name: "北部湾渔场", position: [108.3, 21.5]}
    ];

    // 添加渔场标记
    fishingAreas.forEach(area => {
      const marker = new window.AMap.Marker({
        position: area.position,
        title: area.name
      });
      marker.on('click', () => {
        map.setZoomAndCenter(15, area.position); // 放大到15级并居中
      });
      map.add(marker);
    });

    return () => {
      map.destroy();
    };
  }, []);

  return (
    <Card className="map-card">
      <div className="map-title">海洋牧场定位展示</div>
      <div id="map-container" className="map-container">
        {/* 地图容器 */}
      </div>
    </Card>
  );
};

export default MapSection;
