// src/pages/SmartCenter/WeatherAlert.jsx
import React, { useEffect, useState } from 'react';
import { 
  Card, 
  Typography, 
  Alert, 
  Space, 
  Spin,
  message
} from 'antd';
import {
  ThunderboltOutlined,
  CloudOutlined,
  WarningOutlined,
  ExperimentOutlined
} from '@ant-design/icons';
import './WeatherAlert.css';

const { Title, Text } = Typography;

const WeatherAlert = ({ selectedPoint }) => {
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (selectedPoint) {
      getWeatherByLocation([selectedPoint.lng, selectedPoint.lat]);
    } else {
      // ÈªòËÆ§ÊòæÁ§∫ËàüÂ±±Ê∏îÂú∫Â§©Ê∞î
      getWeatherByLocation([122.2, 29.98]);
    }
  }, [selectedPoint]);

  const getWeatherByLocation = (lnglat) => {
    setLoading(true);
    const [lng, lat] = lnglat;

    // Áõ¥Êé•‰ΩøÁî®ÁªèÁ∫¨Â∫¶Êü•ËØ¢Â§©Ê∞î
    fetch(`https://restapi.amap.com/v3/weather/weatherInfo?key=9238743e5d83039e6f3486a437f5eb0d&location=${lng},${lat}&extensions=base`)
      .then(res => {
        if (!res.ok) throw new Error('Â§©Ê∞îËØ∑Ê±ÇÂ§±Ë¥•');
        return res.json();
      })
      .then(data => {
        console.log('Â§©Ê∞îAPIËøîÂõû:', data);
        if (data.status === '1' && data.lives?.length > 0) {
          setWeatherData(data.lives[0]);
        } else {
          // Ê∑ªÂä†Â§áÁî®ÊñπÊ°àÔºö‰ΩøÁî®ËàüÂ±±‰Ωú‰∏∫ÈªòËÆ§‰ΩçÁΩÆ
          if (!selectedPoint) {
            return fetch(`https://restapi.amap.com/v3/weather/weatherInfo?key=9238743e5d83039e6f3486a437f5eb0d&city=330900`);
          }
          throw new Error('Êó†Ê≥ïËé∑ÂèñÂΩìÂâç‰ΩçÁΩÆÂ§©Ê∞îÊï∞ÊçÆ');
        }
      })
      .then(backupData => {
        if (backupData) {
          return backupData.json().then(data => {
            if (data.status === '1' && data.lives?.length > 0) {
              setWeatherData(data.lives[0]);
              message.info('Â∑≤ÊòæÁ§∫ÈªòËÆ§Ê∏îÂú∫Â§©Ê∞î');
            }
          });
        }
      })
      .catch(error => {
        console.error('ËØ∑Ê±ÇÂ§±Ë¥•:', error);
        message.error('Ëé∑ÂèñÂ§©Ê∞îÊï∞ÊçÆÂ§±Ë¥•: ' + error.message);
      })
      .finally(() => setLoading(false));
  };

  return (
    <Card className="weather-alert-card">
      <Title level={4} className="weather-alert-title">Â§©Ê∞îÈ¢ÑË≠¶</Title>
      {selectedPoint && (
        <div style={{ marginBottom: 16 }}>
          <Text strong style={{ color: '#00eaff' }}>ÂΩìÂâçÊ∏îÂú∫: {selectedPoint.name}</Text>
          <div style={{ marginTop: 8 }}>
            <Text style={{ color: '#fff' }}>Èù¢ÁßØ: {selectedPoint.areaSize}</Text>
            <Text style={{ color: '#fff', marginLeft: 16 }}>ÂÖªÊÆñÁ±ªÂûã: {selectedPoint.waterType}</Text>
            <Text style={{ color: '#fff', display: 'block', marginTop: 4 }}>
              ‰∏ªË¶ÅÈ±ºÁßç: {selectedPoint.fishTypes?.join('„ÄÅ')}
            </Text>
          </div>
        </div>
      )}
      {loading ? (
        <Spin tip="Âä†ËΩΩÂ§©Ê∞î‰ø°ÊÅØ‰∏≠..." />
      ) : (
        <>
          <div className="weather-status">
            <div className="weather-icon-wrapper">
              <CloudOutlined className="weather-icon" />
            </div>
            <div className="weather-details">
              <Text className="weather-info" style={{ color: '#fff' }}>üå° ÂΩìÂâçÊ∏©Â∫¶Ôºö{weatherData?.temperature ?? '--'}¬∞C</Text>
              <Text className="weather-info" style={{ color: '#fff' }}>üå¨ È£éÈÄüÔºö{weatherData?.windpower ?? weatherData?.windSpeed ?? '--'} Á∫ß</Text>
              <Text className="weather-info" style={{ color: '#fff' }}>üß≠ È£éÂêëÔºö{weatherData?.winddirection ?? '--'}</Text>
              <Text className="weather-info" style={{ color: '#fff' }}>üíß ÊπøÂ∫¶Ôºö{weatherData?.humidity ?? '--'}%</Text>
            </div>
          </div>

          <div className="ai-advice">
            <div className="ai-advice-header">
              <ExperimentOutlined className="ai-icon" />
              <Text className="ai-title">AI Êô∫ËÉΩÂª∫ËÆÆ</Text>
            </div>
            <div className="ai-text">
              <Text className="weather-info" style={{ color: '#fff' }}>üå° Ê∏©Â∫¶Âª∫ËÆÆÔºö{weatherData?.recommendation?.temperature ?? '--'}</Text>
              <Text className="weather-info" style={{ color: '#fff' }}>üß™ pH Âª∫ËÆÆÔºö{weatherData?.recommendation?.ph ?? '--'}</Text>
              <Text className="weather-info" style={{ color: '#fff' }}>üßÇ ÁõêÂ∫¶Âª∫ËÆÆÔºö{weatherData?.recommendation?.salinity ?? '--'}</Text>
              <Text className="weather-info" style={{ color: '#fff' }}>‚òÄÔ∏è ÂÖâÁÖßÂª∫ËÆÆÔºö{weatherData?.recommendation?.light ?? '--'}</Text>
              <Text className="weather-info" style={{ color: '#fff' }}>üíß Ê∫∂Ê∞ßÂª∫ËÆÆÔºö{weatherData?.recommendation?.oxygen ?? '--'}</Text>
            </div>
          </div>

          <Space direction="vertical" className="weather-warning-list">
            {weatherData?.alerts?.length > 0 && weatherData.alerts.map((alert, index) => (
              <Alert
                key={index}
                message={<span style={{ fontSize: '16px', color: '#fff' }}>{alert.message}</span>}
                type={alert.type} // "warning" | "error" | "info"
                showIcon
                icon={alert.type === 'error' ? <ThunderboltOutlined /> : <WarningOutlined />}
              />
            ))}
          </Space>
        </>
      )}
      {!loading && !weatherData && (
        <Alert
          message="ÂΩìÂâçÂå∫ÂüüÂ§©Ê∞îÊï∞ÊçÆ‰∏çÂèØÁî®ÔºåËØ∑ÂèÇËÄÉÈôÑËøëÊ∏îÂú∫Âª∫ËÆÆ"
          type="info"
          showIcon
        />
      )}
    </Card>
  );
};

export default WeatherAlert;
