// src/pages/SmartCenter/WeatherAlert.jsx
import React, { useEffect, useState } from 'react';
import { 
  Card, 
  Typography, 
  Alert, 
  Space, 
  Spin,
  message
} from 'antd';
import {
  ThunderboltOutlined,
  CloudOutlined,
  WarningOutlined,
  ExperimentOutlined
} from '@ant-design/icons';
import './WeatherAlert.css';

const { Title, Text } = Typography;

const WeatherAlert = ({ selectedPoint }) => {
  const [weatherData, setWeatherData] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    if (selectedPoint) {
      getWeatherByLocation([selectedPoint.lng, selectedPoint.lat]);
    } else {
      // 默认显示舟山渔场天气
      getWeatherByLocation([122.2, 29.98]);
    }
  }, [selectedPoint]);

  const FISHING_AREAS = {
    '渤海渔场': { lng: 120.730618, lat: 40.152939 }, // 小龙山岛精确坐标
    '南海渔场': { lng: 112.887598, lat: 20.613384 }, // 正咀山精确坐标
    '北部湾渔场': { lng: 109.47646, lat: 21.48482 }   // 广旅白沙湾展示中心
  };

  const getWeatherByLocation = (lnglat) => {
    setLoading(true);
    const [lng, lat] = lnglat;

    fetch(`https://restapi.amap.com/v3/weather/weatherInfo?key=9238743e5d83039e6f3486a437f5eb0d&location=${lng},${lat}&extensions=base`)
      .then(res => res.json())
      .then(data => {
        if (data.status === '1' && data.lives?.length > 0) {
          const weather = data.lives[0];
          const specialRecommendation = generateSpecialRecommendation(weather);
          setWeatherData({
            ...weather,
            recommendation: {
              ...generateRecommendations(weather),
              ...specialRecommendation
            }
          });
        } else {
          throw new Error('天气数据不可用');
        }
      })
      .catch(() => {
        const backupLnglat = [122.2, 29.98]; // 舟山备用坐标
        fetch(`https://restapi.amap.com/v3/weather/weatherInfo?key=9238743e5d83039e6f3486a437f5eb0d&location=${backupLnglat[0]},${backupLnglat[1]}`)
          .then(res => res.json())
          .then(backupData => {
            if (backupData.status === '1') {
              setWeatherData({
                ...backupData.lives[0],
                isBackup: true
              });
              message.warning('当前渔场数据异常，已显示舟山参考天气');
            }
          });
      })
      .finally(() => setLoading(false));
  };

  const generateSpecialRecommendation = (weather) => {
    const area = Object.keys(FISHING_AREAS).find(key => 
      Math.abs(FISHING_AREAS[key].lng - weather.longitude) < 0.1 &&
      Math.abs(FISHING_AREAS[key].lat - weather.latitude) < 0.1
    );

    const fishTypeMap = {
      '渤海渔场': ['扇贝', '海参', '鲍鱼'],
      '南海渔场': ['金枪鱼', '石斑鱼', '军曹鱼'],
      '北部湾渔场': ['对虾', '青蟹', '牡蛎']
    };

    const recommendations = {
      '渤海渔场': {
        salinity: '当前盐度适合贝类养殖',
        oxygen: '建议每日增氧4小时以上',
        fishType: fishTypeMap['渤海渔场']
      },
      '南海渔场': {
        temperature: '水温适合热带鱼类生长',
        light: '建议增加遮阳设施',
        fishType: fishTypeMap['南海渔场']
      },
      '北部湾渔场': {
        ph: '注意监测海水酸化情况',
        salinity: '盐度适合对虾养殖',
        fishType: fishTypeMap['北部湾渔场']
      }
    };

    return area ? recommendations[area] : {};
  };

  return (
    <Card className="weather-alert-card">
      <Title level={4} className="weather-alert-title">天气预警</Title>
      {selectedPoint && (
        <div style={{ marginBottom: 16 }}>
          <Text strong style={{ color: '#00eaff' }}>当前渔场: {selectedPoint.name}</Text>
          <div style={{ marginTop: 8 }}>
            <Text style={{ color: '#fff' }}>面积: {selectedPoint.areaSize}</Text>
            <Text style={{ color: '#fff', marginLeft: 16 }}>养殖类型: {selectedPoint.waterType}</Text>
            <Text style={{ color: '#fff', display: 'block', marginTop: 4 }}>
              主要鱼种: {selectedPoint.fishTypes?.join('、')}
            </Text>
          </div>
        </div>
      )}
      {loading ? (
        <Spin tip="加载天气信息中..." />
      ) : (
        <>
          <div className="weather-status">
            <div className="weather-icon-wrapper">
              <CloudOutlined className="weather-icon" />
            </div>
            <div className="weather-details">
              <Text className="weather-info" style={{ color: '#fff' }}>🌡 当前温度：{weatherData?.temperature ?? '--'}°C</Text>
              <Text className="weather-info" style={{ color: '#fff' }}>🌬 风速：{weatherData?.windpower ?? weatherData?.windSpeed ?? '--'} 级</Text>
              <Text className="weather-info" style={{ color: '#fff' }}>🧭 风向：{weatherData?.winddirection ?? '--'}</Text>
              <Text className="weather-info" style={{ color: '#fff' }}>💧 湿度：{weatherData?.humidity ?? '--'}%</Text>
            </div>
          </div>

          <div className="ai-advice">
            <div className="ai-advice-header">
              <ExperimentOutlined className="ai-icon" />
              <Text className="ai-title">AI 智能建议</Text>
            </div>
            <div className="ai-text">
              <Text className="weather-info" style={{ color: '#fff' }}>🌡 温度建议：{weatherData?.recommendation?.temperature ?? '--'}</Text>
              <Text className="weather-info" style={{ color: '#fff' }}>🧪 pH 建议：{weatherData?.recommendation?.ph ?? '--'}</Text>
              <Text className="weather-info" style={{ color: '#fff' }}>🧂 盐度建议：{weatherData?.recommendation?.salinity ?? '--'}</Text>
              <Text className="weather-info" style={{ color: '#fff' }}>☀️ 光照建议：{weatherData?.recommendation?.light ?? '--'}</Text>
              <Text className="weather-info" style={{ color: '#fff' }}>💧 溶氧建议：{weatherData?.recommendation?.oxygen ?? '--'}</Text>
            </div>
          </div>

          <Space direction="vertical" className="weather-warning-list">
            {weatherData?.alerts?.length > 0 && weatherData.alerts.map((alert, index) => (
              <Alert
                key={index}
                message={<span style={{ fontSize: '16px', color: '#fff' }}>{alert.message}</span>}
                type={alert.type} // "warning" | "error" | "info"
                showIcon
                icon={alert.type === 'error' ? <ThunderboltOutlined /> : <WarningOutlined />}
              />
            ))}
          </Space>
        </>
      )}
      {!loading && !weatherData && (
        <Alert
          message="当前区域天气数据不可用，请参考附近渔场建议"
          type="info"
          showIcon
        />
      )}
      {weatherData?.isBackup && (
        <Alert
          message="特别提示"
          description="当前显示的是参考渔场数据，实际天气可能略有差异"
          type="warning"
          showIcon
          style={{ marginBottom: 16 }}
        />
      )}
    </Card>
  );
};

export default WeatherAlert;
